mapper.select:
select * from (
	select sub.*, rownum as rnum from(
		select * from HOUSE
		where HOUSE_ADDRESS LIKE '%'||#{key}||'%'
		order by HOUSE_NO desc
	) sub
) where rnum >= #{low,jdbcType=NUMERIC} and rnum <= #{high,jdbcType=NUMERIC}

mapper.update:
update HOUSE set
HOUSE_NAME=#{houseName}, HOUSE_INFO=#{houseInfo}, HOUSE_ADDRESS=#{houseAddress}, HOUSE_PRICE=#{housePrice},
HOUSE_ROOM=#{houseRoom}, HOUSE_BATH=#{houseBath}, HOUSE_HOSTING=#{houseHosting}, HOUSE_TV=#{houseTv}, 
HOUSE_AIRCON=#{houseAircon}, HOUSE_WIFI=#{houseWifi}, HOUSE_ELEBE=#{houseElebe},
HOUSE_WASHING=#{houseWashing}, HOUSE_IMG=#{houseImg}, HOUSE_DAY=#{houseDay}
where house_no=#{houseNo}

